import React, { useState } from "react";
import {
  Dialog,
  DialogTitle,
  DialogContent,
  TextField,
  DialogActions,
  Button,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  IconButton,
  Typography,
  Box,
  Avatar,
} from "@mui/material";
import { Close, CheckBoxOutlineBlank, CheckBox, Search } from "@mui/icons-material"; // Import Search icon

const ExtraFacility = ({ open, onClose, onAddFacility }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedItems, setSelectedItems] = useState([]);

  // Items with unique image links
  const items = [
    { name: "Projector", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSulex_5vAtTBG41ojyG-08LHKoocoH95ctWQ&s" },
    { name: "Guest chair", image: "data:image/jpeg;base64," },
    { name: "Dais table", image: "data:image/jpeg;base64," },
    { name: "White board", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQroRj36oJAOblAoHJaDqDcSgb1vf5D9xmnNQ&s" },
    { name: "Mic", image: "data:image/jpeg;base64," },
    { name: "Chairs", image: "data:image/jpeg;base64," },
    { name: "Charging ports", image: "data:image/jpeg;base64," },
    { name: "Table", image: "data:image/jpeg;base64," },
  ];

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleToggleItem = (item) => {
    if (selectedItems.includes(item)) {
      setSelectedItems(selectedItems.filter((i) => i !== item));
    } else {
      setSelectedItems([...selectedItems, item]);
    }
  };

  const handleDeselectAll = () => {
    setSelectedItems([]);
  };

  const handleAdd = () => {
    onAddFacility(selectedItems); // Pass selected items to the parent
    setSelectedItems([]); // Clear selected items
    onClose(); // Close the popup
  };

  const filteredItems = items.filter((item) =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <Dialog
      open={open}
      onClose={onClose}
      fullWidth
      maxWidth="sm"
      PaperProps={{
        sx: {
          borderRadius: "12px", // Rounded corners for the outer container
          boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)", // Shadow for the outer container
          backgroundColor: "#f5f6fa", // Light grey background for the outer container
        },
      }}
    >
      <DialogTitle>
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Typography variant="h5" sx={{ fontWeight: "bold" }}>
            Add Infra
          </Typography>
          <IconButton onClick={onClose} sx={{ color: "red" }}> {/* Red close button */}
            <Close />
          </IconButton>
        </Box>
        {/* Grey Line Below Add Infra */}
        <Box
          sx={{
            borderBottom: "1px solid #e0e0e0", // Grey line
            marginTop: "10px", // Space below the title
          }}
        />
      </DialogTitle>
      <DialogContent>
        {/* Search Bar with Search Icon */}
        <TextField
          fullWidth
          placeholder="Search"
          value={searchQuery}
          onChange={handleSearchChange}
          sx={{ marginBottom: 2 }}
          InputProps={{
            startAdornment: (
              <Search sx={{ color: "gray", marginRight: 1 }} /> // Search icon
            ),
            sx: {
              borderRadius: "20px", // Rounded search bar
              backgroundColor: "white", // White background
              boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)", // Shadow for input box
            },
          }}
        />
        {/* List of Items */}
        <List>
          {filteredItems.map((item) => (
            <ListItem key={item.name} disablePadding>
              <ListItemButton
                onClick={() => handleToggleItem(item.name)}
                sx={{
                  backgroundColor: "white", // White background for each item
                  borderRadius: "8px", // Rounded corners for each item
                  border: "1px solid #e0e0e0", // Light border for each item
                  marginBottom: "8px", // Space between items
                  padding: "20px 24px", // Increased padding for each item
                  boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)", // Shadow for each item
                  height: "80px", // Increased height of each container
                }}
              >
                {/* Tick Box First */}
                <Box
                  sx={{
                    width: "24px",
                    height: "24px",
                    borderRadius: "50%", // Round checkbox
                    border: "2px solid #4caf50", // Green border
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    backgroundColor: selectedItems.includes(item.name) ? "#4caf50" : "transparent", // Green background when selected
                  }}
                >
                  {selectedItems.includes(item.name) && (
                    <CheckBox sx={{ color: "white", fontSize: "18px" }} /> // White tick when selected
                  )}
                </Box>
                {/* Image Second */}
                <Avatar
                  src={item.image} // Use the image link from the item object
                  sx={{ width: 40, height: 40, marginLeft: 2, marginRight: 2 }} // Adjust size and spacing
                />
                {/* Bold Text for Items */}
                <ListItemText
                  primary={item.name}
                  sx={{ marginLeft: 2, fontWeight: "bold" }} // Bold text
                />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </DialogContent>
      <DialogActions sx={{ justifyContent: "space-between", padding: 2 }}>
        {/* Deselect All and Selected Count */}
        <Box display="flex" alignItems="center" gap={2}>
          <Button onClick={handleDeselectAll} sx={{ color: "red" }}>
            Deselect All
          </Button>
          <Typography>Selected {selectedItems.length}</Typography>
        </Box>
        {/* Add Button in Green */}
        <Button
          variant="contained"
          onClick={handleAdd}
          sx={{
            backgroundColor: "green",
            color: "white",
            width: "180px", // Increased width of the Add button
          }}
        >
          Add
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default ExtraFacility;